<script src="https://cdn.jsdelivr.net/npm/chart.js"></script>
<script>
    // Připrav data z user_stats
    const stats = {{ stats|tojson }};
    // Předpokládáme, že user_stats má sloupce: id, user_id, stat_name, stat_value
    const labels = stats.map(s => s.stat_name);
    const values = stats.map(s => s.stat_value);
    const ctx = document.getElementById('statsChart').getContext('2d');
    new Chart(ctx, {
        type: 'bar',
        data: {
            labels: labels,
            datasets: [{
                label: 'Hodnota',
                data: values,
                backgroundColor: 'rgba(54, 162, 235, 0.5)',
                borderColor: 'rgba(54, 162, 235, 1)',
                borderWidth: 1
            }]
        },
        options: {
            responsive: true,
            scales: {
                y: {
                    beginAtZero: true
                }
            }
        }
    });
</script>
